        public string GetName(string name, int size)
        {
            int count = name.Length;

            var center = size / 2;
            var min = center - count / 2;
            var max = center + count / 2;

            string retSTR = "";

            bool isNameTyping = false;
            for (int i = 0; i < size; i++)
            {
                if (i < min || i > max)
                {
                    retSTR += " ";
                }
                else if (!isNameTyping)
                {
                    retSTR += name;
                    isNameTyping = true;
                }
            }
            return retSTR;
        }

